componentsModule.modules.comments={start:function(){let current=componentsModule.modules.comments;current.parentElement=document.querySelector("[data-component=comments]");current.commentsCheck=current.parentElement.querySelector("[name=component_comments]");current.approveBox=current.parentElement.querySelector(".component_comments_approve");current.approveCheck=current.approveBox.querySelector("input");componentsModule.initializeEvent({event:"change",element:current.commentsCheck,content:function(){if(!!(current.commentsCheck.checked)){current.approveBox.classList.remove("hide")}else{current.approveBox.classList.add("hide")}reloadPackery()}})},resume:function(){let current=componentsModule.modules.comments;const a=current.parentElement.getAttribute("data-resume");if(a===""){return true}const b=JSON.parse(a);current.commentsCheck.checked=!!(b.comments);triggerEvent(current.commentsCheck,"change");current.approveCheck.checked=(b.comments_approve)},validate:function(){return true},serialize:function(){let current=componentsModule.modules.comments;return{comments:!!(current.commentsCheck.checked),comments_approve:!!(current.approveCheck.checked)}}};