function Closer(){this.stack=[];this.counter=0}Closer.prototype.addElement=function(b){this.stack.push(b[0]);this.counter=1};Closer.prototype.closeElements=function(b){if(this.counter>0){this.counter=0;return false}for(let item in this.stack){let element=this.stack[item];if($(element).find(b).length>0||$(element).is($(b))){}else{$(element).removeClass("open");this.stack.splice(item,1)}}};