function Translate(a,b){this.local=b;this.locale={};for(let i in a){let domain=a[i];let language;if(localStorage.getItem("locale_"+this.local)){language=localStorage.getItem("locale_"+this.local)}else{language=$("html").attr("lang")}this.language=language;this.locale[domain]=getJson("app/Data/Locale/"+language+"/"+domain+".json");this.changeLanguage(language,this.locale[domain])}}Translate.prototype.getLanguage=function(){return this.language};Translate.prototype.getLocale=function(){return this.locale};Translate.prototype.changeLanguage=function(b,a){localStorage.setItem("locale_"+this.local,b);$("html").attr("lang",a.LOCALE);$("[data-locale]").each(function(){let phrase=$(this).data("locale");$('[data-locale="'+phrase+'"]').html(a[phrase])});$("[data-placeholder]").each(function(){let phrase=$(this).data("placeholder");$('[data-placeholder="'+phrase+'"]').attr("placeholder",a[phrase])})};