function Translate(d,c){this.local=c;this.locale={};for(let i in d){let domain=d[i];let language;if(localStorage.getItem("locale_"+this.local)){language=localStorage.getItem("locale_"+this.local)}else{language=$("html").attr("lang")}this.language=language;this.locale[domain]=getJson("app/Data/Locale/"+language+"/"+domain+".json");this.changeLanguage(language,this.locale[domain])}}Translate.prototype.getLanguage=function(){return this.language};Translate.prototype.getLocale=function(){return this.locale};Translate.prototype.changeLanguage=function(c,d){localStorage.setItem("locale_"+this.local,c);$("html").attr("lang",d.LOCALE);$("[data-locale]").each(function(){let phrase=$(this).data("locale");$('[data-locale="'+phrase+'"]').html(d[phrase])});$("[data-placeholder]").each(function(){let phrase=$(this).data("placeholder");$('[data-placeholder="'+phrase+'"]').attr("placeholder",d[phrase])})};